/**
 * Format object to media type.
 *
 * @param {object} obj
 * @return {string}
 * @public
 */
export function format(obj: object): string;
/**
 * Parse media type to object.
 *
 * @param {string} string
 * @return {object}
 * @public
 */
export function parse(string: string): object;
/**
 * Test media type.
 *
 * @param {string} string
 * @return {object}
 * @public
 */
export function test(string: string): object;
