/**
 * Format object to media type.
 *
 * @param {object} obj
 * @return {string}
 * @public
 */
export function format(obj: object): string;
/**
 * Parse media type to object.
 *
 * @param {string|object} string
 * @return {Object}
 * @public
 */
export function parse(string: string | object): any;
